function createCookie(name,value,days){if(days){var date=new Date;date.setTime(date.getTime()+24*days*60*60*1e3);var expires="; expires="+date.toGMTString()}else var expires="";document.cookie=name+"="+value+expires+"; path=/"}function readCookie(name){for(var nameEQ=name+"=",ca=document.cookie.split(";"),i=0;i<ca.length;i++){for(var c=ca[i];" "==c.charAt(0);)c=c.substring(1,c.length);if(0==c.indexOf(nameEQ))return c.substring(nameEQ.length,c.length)}return null}function eraseCookie(name){createCookie(name,"",-1)}$(document).ready(function(){"1"!=readCookie("eu-cookies-info")&&$(".cookies-info").delay(1e3).slideDown(500),$(".cookies-info button").click(function(){createCookie("eu-cookies-info","1",30),$(".cookies-info").slideUp()})});